// Contract upgrade
export var authorizeUpgrade = function (address, vaa) {
    if (!address)
        throw new Error("Need bridge address.");
    return {
        function: address + "::contract_upgrade::submit_vaa_entry",
        type_arguments: [],
        arguments: [vaa],
    };
};
export var upgradeContract = function (address, metadataSerialized, code) {
    if (!address)
        throw new Error("Need bridge address.");
    return {
        function: address + "::contract_upgrade::upgrade",
        type_arguments: [],
        arguments: [metadataSerialized, code],
    };
};
export var migrateContract = function (address) {
    if (!address)
        throw new Error("Need bridge address.");
    return {
        function: address + "::contract_upgrade::migrate",
        type_arguments: [],
        arguments: [],
    };
};
